document.addEventListener("DOMContentLoaded", function() {
       setTimeout(function() {
           const popup = document.getElementById('welcome-popup');
           popup.style.display = 'flex';
           // Thêm lớp 'show' sau khi hiển thị popup
           setTimeout(function() {
               popup.classList.add('show');
           }, 10); // Trì hoãn ngắn để kích hoạt hiệu ứng
       }, 2000); // Hiển thị sau 2 giây

       document.getElementById('close-popup').onclick = function() {
           const popup = document.getElementById('welcome-popup');
           popup.classList.remove('show');
           // Ẩn popup sau khi hiệu ứng mờ dần kết thúc
           setTimeout(function() {
               popup.style.display = 'none';
           }, 500); // Thời gian khớp với transition trong CSS
       }
   });

// Music list
const HongLin_List = [
    "https://dev.htpn.vn/upload/gods.mp3", // GODS
    "https://files.catbox.moe/law377.mp3" // TUONG TU
];

// Randomly select a song from the list
let index = Math.floor(Math.random() * HongLin_List.length);
const audio = new Audio(HongLin_List[index]);

// Function to play audio on document click
function HongLinAudio() {
    audio.play();
    document.removeEventListener("click", HongLinAudio); // Remove event listener after the first play
}

// Add event listener to play audio on click
document.addEventListener("click", HongLinAudio);

// Event listener for when the audio ends
audio.addEventListener("ended", function() {
    index = (index + 1) % HongLin_List.length; // Loop to the next song in the list
    audio.src = HongLin_List[index];
    audio.play();
});

// Function to start playing audio immediately on page load
function HongLinStart() {
    audio.play();
}

// Start playing audio on page load
window.addEventListener("load", HongLinStart);

      // Lấy tất cả các thẻ  trên trang
var images = document.getElementsByTagName("img");

// Duyệt qua từng thẻ  và thêm thuộc tính loading="lazy"
for (var i = 0; i < images.length; i++) {
	images[i].setAttribute("loading", "lazy");
}

// Lấy tất cả các phần tử có class là "meta"
var metaElements = document.getElementsByClassName("meta");

// Duyệt qua từng phần tử và thêm thuộc tính translate="no"
for (var i = 0; i <  metaElements.length; i++) {
	metaElements[i].setAttribute("translate", "no");
}

// Hiển thị hoặc ẩn nút "Back to Top" khi cuộn trang
window.onscroll = function () {
	toggleBackToTopButton();
};

function toggleBackToTopButton() {
	const button = document.getElementById("back-to-top");
	if (
		document.body.scrollTop > 200 ||
		document.documentElement.scrollTop > 200
	) {
		button.style.display = "block";
	} else {
		button.style.display = "none";
	}
}

function scrollToBento() {
	const bentoElement = document.getElementById("wrapper");
	bentoElement.scrollIntoView({ behavior: "smooth" });
}

window.addEventListener("scroll", function () {
	const scrollButton = document.getElementById("scroll-to-bento");
	if (window.scrollY > 100) {
		// Adjust the value to control when to hide the button
		scrollButton.style.display = "none";
	} else {
		scrollButton.style.display = "block";
	}
});

// Cuộn về đầu trang khi nhấp vào nút
function scrollToTop() {
	window.scrollTo({
		top: 0,
		behavior: "smooth",
	});
}

function updateAboutWidth() {
	var pro = document.getElementById("profile");
	var about = document.getElementById("about-me");
	var proWidth = pro.offsetWidth;
	about.style.width = proWidth + "px";
}

// Update width on page load
window.addEventListener("load", updateAboutWidth);

// Update width on window resize
window.addEventListener("resize", updateAboutWidth);

function toggleOpacity() {
	var bubbleTrigger = document.querySelector(".bubble-trigger");
	bubbleTrigger.classList.toggle("active"); // Toggle class active khi click vào .bubble-trigger
}

      const userID = "738748102311280681"; // Thay đổi thành ID Discord của bạn
const elements = {
	statusDot: document.querySelector("#dot"),
	ping: document.querySelector("#ping"),
	statusText: document.querySelector("#status p"),
};

const statusColorMap = {
	online: { color: "#23a55a", text: "Online", glow: "0 0 5px 2px #23a55a" },
	dnd: {
		color: "#f23f43",
		text: "Do Not Disturb",
		glow: "0 0 5px 2px #f23f43",
	},
	idle: { color: "#f0b232", text: "Idle / AFK", glow: "0 0 5px 2px #f0b232" },
	offline: { color: "#7e828c", text: "Offline", glow: "none" },
	streaming: {
		color: "#593695",
		text: "Streaming",
		glow: "0 0 5px 2px #593695",
	},
};

async function fetchDiscordStatus() {
	try {
		const response = await fetch(`https://api.lanyard.rest/v1/users/${userID}`);
		const lanyardResponse = await response.json();
		const { data: lanyardData } = lanyardResponse;
		const { discord_status, activities } = lanyardData;

		let { color, text, glow } = statusColorMap[discord_status] || {
			color: "#7e828c",
			text: "Unknown",
			glow: "none",
		};

		if (
			activities.some(
				(activity) =>
					activity.type === 1 &&
					(activity.url.includes("twitch.tv") ||
						activity.url.includes("youtube.com"))
			)
		) {
			color = "#593695";
			text = "Streaming";
			glow = "0 0 5px 2px #593695";
		}

		elements.statusDot.style.backgroundColor = color;
		elements.ping.style.backgroundColor = color;
		elements.ping.style.boxShadow = glow;
		elements.statusText.textContent = text;
	} catch (error) {
		console.error("Không thể lấy trạng thái Discord:", error);
	}
}

// Lấy trạng thái Discord khi trang được tải
fetchDiscordStatus();
// Lấy trạng thái Discord mỗi 10 giây
setInterval(fetchDiscordStatus, 10000);

      // clock function
function updateTime() {
	const timeElement = document.getElementById("time");
	const dayElement = document.getElementById("day");
	const dateElement = document.getElementById("date");

	const now = new Date();
	const utcNow = new Date(now.getTime() + now.getTimezoneOffset() * 60000);
	const localTime = new Date(utcNow.getTime() + 7 * 3600000); // UTC+7

	const hours = String(localTime.getHours()).padStart(2, "0");
	const minutes = String(localTime.getMinutes()).padStart(2, "0");
	const timeString = `${hours}:${minutes}`;
	timeElement.textContent = timeString;

	const days = [
		"Sunday",
		"Monday",
		"Tuesday",
		"Wednesday",
		"Thursday",
		"Friday",
		"Saturday",
	];
	const dayString = days[localTime.getDay()];
	dayElement.textContent = dayString;

	const months = [
		"Jan",
		"Feb",
		"Mar",
		"Apr",
		"May",
		"Jun",
		"Jul",
		"Aug",
		"Sep",
		"Oct",
		"Nov",
		"Dec",
	];
	const dateString = `${localTime.getDate()}, ${months[localTime.getMonth()]}`;
	dateElement.textContent = dateString;
}

// Update the time every second
setInterval(updateTime, 1000);
// Initial call to display the time immediately
updateTime();
